import { useLocation } from "react-router-dom";
import classes from "./CityDetailsTab.module.css";
import { useState, useEffect } from "react";

const CityDetailsTab = () => {
  const location = useLocation();
  const requestedCity = location.state;
  const [selectedView, setSelectedView] = useState("");

  useEffect(() => {
    setSelectedView("");
  }, [requestedCity]);

  console.log(requestedCity);
  return (
    <div className={classes.details}>
      <h2> Meteorological data for {requestedCity.city} </h2>
      <select
        value={selectedView}
        onChange={(event) => {
          setSelectedView(event.target.value);
        }}
      >
        <option value="" key="default">
          Choose your view
        </option>
        <option key="hourly" value="hourlyView">
          Hourly View
        </option>
        <option key="daily" value="dailyView">
          Daily View
        </option>
      </select>
      {selectedView === "hourlyView" && (
        <>
          <h3>Hourly weather variables</h3>
          <div className={classes.variables}>
            <input type="checkbox" id="temperature2m" />
            <label for="temperature2m">Temperature (2m)</label>
            <input type="checkbox" id="cloudcoverTotal" />
            <label for="cloudcoverTotal">Cloudcover Total</label>
            <input type="checkbox" id="windSpeed10m" />
            <label for="windSpeed10m">Wind Speed (10m)</label>
            <input type="checkbox" id="soilTemp0cm" />
            <label for="soilTemp0cm">Soil Temperature (0cm)</label>
            <input type="checkbox" id="relHumidity" />
            <label for="relHumidity">Relative Humidity (2m)</label>
            <input type="checkbox" id="cloudcoverLow" />
            <label for="cloudcoverLow">Cloudcover Low</label>
            <input type="checkbox" id="windSpeed80m" />
            <label for="windSpeed80m">Wind Speed (80m)</label>
            <input type="checkbox" id="soilTemp6cm" />
            <label for="soilTemp6cm">Soil Temperature (6cm)</label>
            <input type="checkbox" id="dewPoint" />
            <label for="dewPoint">Dew Point (2m)</label>
            <input type="checkbox" id="cloudcoverMid" />
            <label for="cloudcoverMid">Cloudcover Mid</label>
            <input type="checkbox" id="windSpeed120m" />
            <label for="windSpeed120m">Wind Speed (120m)</label>
            <input type="checkbox" id="soilTemp18cm" />
            <label for="soilTemp18cm">Soil Temperature (18cm)</label>
          </div>
        </>
      )}
      {selectedView === "dailyView" && (
        <div className={classes.variables}>
          <h3>Daily weather variables</h3>
        </div>
      )}
    </div>
  );
};

export default CityDetailsTab;
