import { useLocation } from "react-router-dom";
import classes from "./CityDetailsTab.module.css";
import { useState } from "react";

const CityDetailsTab = () => {
  const location = useLocation();
  const requestedCity = location.state;
  const [selectedView, setSelectedView] = useState("");

  console.log(requestedCity);
  return (
    <div className={classes.details}>
      <h2> Meteorological data for {requestedCity.city} </h2>
      <select>
        <option value="" onClick={() => setSelectedView("")}>
          Choose your view
        </option>
        <option
          value="hourlyView"
          onClick={() => setSelectedView("hourlyView")}
        >
          Hourly View
        </option>
        <option value="dailyView" onClick={() => setSelectedView("dailyView")}>
          Daily View
        </option>
      </select>
      <p> {requestedCity.lng} </p>
    </div>
  );
};

export default CityDetailsTab;
