import { useLocation } from "react-router-dom";
import classes from "./CityDetailsTab.module.css";
import { useState, useEffect } from "react";

const CityDetailsTab = () => {
  const location = useLocation();
  const requestedCity = location.state;
  const [selectedView, setSelectedView] = useState("");

  useEffect(() => {
    setSelectedView("");
  }, [requestedCity]);

  console.log(requestedCity);
  return (
    <div className={classes.details}>
      <h2> Meteorological data for {requestedCity.city} </h2>
      <select
        value={selectedView}
        onChange={(event) => {
          setSelectedView(event.target.value);
        }}
      >
        <option value="" key="default">
          Choose your view
        </option>
        <option key="hourly" value="hourlyView">
          Hourly View
        </option>
        <option key="daily" value="dailyView">
          Daily View
        </option>
      </select>
      {selectedView === "hourlyView" && <p> OVO JE SATNI PRIKAZ </p>}
      {selectedView === "dailyView" && <p> OVO JE DNEVNI PRIKAZ </p>}
    </div>
  );
};

export default CityDetailsTab;
