import { useState } from "react";
import classes from "./Settings.module.css";
import timeZones from "../data/timeZones.json";

const Settings = () => {
  const [selectedTimeZone, setSelectedTimeZone] = useState("GMT");

  const timeZoneChangeHandler = (event) => {
    setSelectedTimeZone(event.target.value);
  };

  return (
    <div className={classes.settingsTab}>
      <h1>APPLICATION SETTINGS</h1>
      <h2>Temperature unit</h2>
      <div className={classes.radioButtons}>
        <input
          type="radio"
          id="celsius"
          name="temperature"
          value="celsius"
          defaultChecked
        />
        <label htmlFor="celsius">Celsius °C</label>
        <input
          type="radio"
          id="fahrenheit"
          name="temperature"
          value="fahrenheit"
        />
        <label htmlFor="celsius">Fahrenheit °F</label>
      </div>
      <h2>Wind speed unit</h2>
      <div className={classes.radioButtons}>
        <input
          type="radio"
          id="km/h"
          name="windspeed_unit"
          value="kmh"
          defaultChecked
        />
        <label htmlFor="km/h">km/h</label>
        <input type="radio" id="m/s" name="windspeed_unit" value="ms" />
        <label htmlFor="m/s">m/s</label>
        <input type="radio" id="mph" name="windspeed_unit" value="mph" />
        <label htmlFor="mph">mph</label>
        <input type="radio" id="kn" name="windspeed_unit" value="kn" />
        <label htmlFor="kn">kn</label>
      </div>
      <h2>Precipitation unit</h2>
      <div className={classes.radioButtons}>
        <input
          type="radio"
          id="milimeter"
          name="precipitation_unit"
          value="milimeter"
          defaultChecked
        />
        <label htmlFor="milimeter">Milimeter</label>
        <input type="radio" id="inch" name="precipitation_unit" value="inch" />
        <label htmlFor="inch">Inch</label>
      </div>
      <h2>Timezone</h2>
      <div>
        <select
          name="timeZones"
          value={selectedTimeZone}
          onChange={timeZoneChangeHandler}
        >
          {timeZones.map((zone) => {
            return (
              <option key={zone.label} value={zone.value}>
                {zone.label}
              </option>
            );
          })}
        </select>
      </div>
    </div>
  );
};

export default Settings;
